import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Trash2, Download, Eye, MoreVertical, FileText, Image, File } from 'lucide-react';
import { cn, formatFileSize, formatDate, getFileTypeIcon } from '../utils';
import toast from 'react-hot-toast';

const FileList = ({ files, onDeleteFile, loading = false }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [showMenu, setShowMenu] = useState(null);

  const handleDeleteFile = async (fileId, fileName) => {
    if (window.confirm(`Are you sure you want to delete "${fileName}"?`)) {
      try {
        await onDeleteFile(fileId);
        toast.success('File deleted successfully');
      } catch (error) {
        toast.error('Failed to delete file');
      }
    }
  };

  const getFileIcon = (fileType) => {
    switch (fileType) {
      case 'pdf':
        return <FileText className="w-5 h-5 text-red-500" />;
      case 'txt':
      case 'md':
        return <FileText className="w-5 h-5 text-blue-500" />;
      case 'png':
      case 'jpg':
      case 'jpeg':
      case 'webp':
        return <Image className="w-5 h-5 text-green-500" />;
      default:
        return <File className="w-5 h-5 text-gray-500" />;
    }
  };

  const getFileTypeColor = (fileType) => {
    switch (fileType) {
      case 'pdf':
        return 'bg-red-50 text-red-700 border-red-200';
      case 'txt':
      case 'md':
        return 'bg-blue-50 text-blue-700 border-blue-200';
      case 'png':
      case 'jpg':
      case 'jpeg':
      case 'webp':
        return 'bg-green-50 text-green-700 border-green-200';
      default:
        return 'bg-gray-50 text-gray-700 border-gray-200';
    }
  };

  if (loading) {
    return (
      <div className="space-y-3">
        {[...Array(3)].map((_, i) => (
          <div key={i} className="animate-pulse">
            <div className="bg-gray-200 rounded-lg h-16"></div>
          </div>
        ))}
      </div>
    );
  }

  if (files.length === 0) {
    return (
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="text-center py-8"
      >
        <div className="w-16 h-16 mx-auto mb-4 bg-gray-100 rounded-full flex items-center justify-center">
          <FileText className="w-8 h-8 text-gray-400" />
        </div>
        <p className="text-gray-500 text-sm">No files uploaded yet</p>
        <p className="text-gray-400 text-xs mt-1">Upload documents to get started</p>
      </motion.div>
    );
  }

  return (
    <div className="space-y-3">
      <AnimatePresence>
        {files.map((file, index) => (
          <motion.div
            key={file.id}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, x: -100 }}
            transition={{ duration: 0.2, delay: index * 0.05 }}
            className="group relative bg-white rounded-lg border border-gray-200 hover:border-gray-300 hover:shadow-sm transition-all duration-200"
          >
            <div className="p-4">
              <div className="flex items-start justify-between">
                <div className="flex items-start space-x-3 flex-1 min-w-0">
                  {/* File Icon */}
                  <div className="flex-shrink-0 mt-0.5">
                    {getFileIcon(file.fileType)}
                  </div>

                  {/* File Info */}
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center space-x-2 mb-1">
                      <h3 className="text-sm font-medium text-gray-900 truncate">
                        {file.filename || file.originalName}
                      </h3>
                      <span className={cn(
                        "inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium border",
                        getFileTypeColor(file.fileType)
                      )}>
                        {file.fileType.toUpperCase()}
                      </span>
                    </div>

                    <div className="flex items-center space-x-4 text-xs text-gray-500">
                      <span>{formatFileSize(file.fileSize)}</span>
                      <span>•</span>
                      <span>{formatDate(file.uploadDate)}</span>
                      {file.vectorCount && (
                        <>
                          <span>•</span>
                          <span>{file.vectorCount} chunks</span>
                        </>
                      )}
                    </div>
                  </div>
                </div>

                {/* Actions */}
                <div className="flex items-center space-x-1 opacity-0 group-hover:opacity-100 transition-opacity">
                  <button
                    onClick={() => setSelectedFile(file)}
                    className="p-1.5 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
                    title="View details"
                  >
                    <Eye className="w-4 h-4" />
                  </button>

                  <button
                    onClick={() => handleDeleteFile(file.id, file.filename || file.originalName)}
                    className="p-1.5 text-gray-400 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                    title="Delete file"
                  >
                    <Trash2 className="w-4 h-4" />
                  </button>
                </div>
              </div>
            </div>

            {/* Processing Status */}
            {file.status === 'processing' && (
              <div className="absolute inset-0 bg-white bg-opacity-90 rounded-lg flex items-center justify-center">
                <div className="text-center">
                  <div className="w-6 h-6 mx-auto mb-2">
                    <svg className="animate-spin w-6 h-6 text-primary-600" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                  </div>
                  <p className="text-xs text-gray-600">Processing...</p>
                </div>
              </div>
            )}
          </motion.div>
        ))}
      </AnimatePresence>

      {/* File Details Modal */}
      {selectedFile && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50"
          onClick={() => setSelectedFile(null)}
        >
          <motion.div
            initial={{ scale: 0.95, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.95, opacity: 0 }}
            className="bg-white rounded-xl p-6 max-w-md w-full"
            onClick={(e) => e.stopPropagation()}
          >
            <div className="flex items-center space-x-3 mb-4">
              {getFileIcon(selectedFile.fileType)}
              <div>
                <h3 className="text-lg font-semibold text-gray-900">
                  {selectedFile.filename || selectedFile.originalName}
                </h3>
                <p className="text-sm text-gray-500">File Details</p>
              </div>
            </div>

            <div className="space-y-3 text-sm">
              <div className="flex justify-between">
                <span className="text-gray-500">Type:</span>
                <span className="font-medium">{selectedFile.fileType.toUpperCase()}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-500">Size:</span>
                <span className="font-medium">{formatFileSize(selectedFile.fileSize)}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-500">Uploaded:</span>
                <span className="font-medium">{formatDate(selectedFile.uploadDate)}</span>
              </div>
              {selectedFile.vectorCount && (
                <div className="flex justify-between">
                  <span className="text-gray-500">Chunks:</span>
                  <span className="font-medium">{selectedFile.vectorCount}</span>
                </div>
              )}
            </div>

            <div className="flex space-x-3 mt-6">
              <button
                onClick={() => setSelectedFile(null)}
                className="flex-1 btn-secondary"
              >
                Close
              </button>
              <button
                onClick={() => handleDeleteFile(selectedFile.id, selectedFile.filename || selectedFile.originalName)}
                className="flex-1 bg-red-600 hover:bg-red-700 text-white font-medium py-2 px-4 rounded-lg transition-colors"
              >
                Delete
              </button>
            </div>
          </motion.div>
        </motion.div>
      )}
    </div>
  );
};

export default FileList;
