import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { motion, AnimatePresence } from 'framer-motion';
import { Toaster } from 'react-hot-toast';
import { Send, Menu, X, MessageSquare, Upload as UploadIcon, FileText, Bot, User, Settings } from 'lucide-react';
import toast from 'react-hot-toast';

// Components
import ChatMessage from './components/ChatMessage';
import FileUpload from './components/FileUpload';
import FileList from './components/FileList';
import VoiceInput from './components/VoiceInput';

// Services
import voiceService from './services/voiceService';

// Utils
import { cn, generateId } from './utils';

const API_URL = 'http://localhost:5000/api';

function App() {
  // State for user authentication
  const [user, setUser] = useState({ id: '123456789012', name: 'Demo User' });

  // State for chat
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [chatId, setChatId] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isTyping, setIsTyping] = useState(false);

  // State for file management
  const [files, setFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [filesLoading, setFilesLoading] = useState(false);

  // State for voice
  const [isSpeaking, setIsSpeaking] = useState(false);

  // UI State
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [activeTab, setActiveTab] = useState('upload'); // 'upload' or 'files'

  // Refs
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);
  const chatContainerRef = useRef(null);

  // Initialize with greeting message
  useEffect(() => {
    setMessages([
      {
        id: generateId(),
        role: 'assistant',
        content: 'ðŸ‘‹ Hello! I\'m your AI assistant powered by advanced RAG technology. Upload documents and ask me questions - I\'ll provide contextual answers based on your files. You can also use voice input for a hands-free experience!',
        timestamp: new Date(),
      }
    ]);

    // Fetch user's files
    fetchFiles();
  }, []);

  // Scroll to bottom of messages
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  // Auto-focus input when not loading
  useEffect(() => {
    if (!isLoading && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isLoading]);

  // Fetch user's files
  const fetchFiles = async () => {
    try {
      setFilesLoading(true);
      const response = await axios.get(`${API_URL}/files?userId=${user.id}`);
      setFiles(response.data.files || []);
    } catch (error) {
      console.error('Error fetching files:', error);
      toast.error('Failed to fetch files');
    } finally {
      setFilesLoading(false);
    }
  };

  // Handle file upload
  const handleFileUpload = async (file) => {
    if (!file) return;

    setUploading(true);
    setUploadProgress(0);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('userId', user.id);

    try {
      const response = await axios.post(`${API_URL}/files/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setUploadProgress(percentCompleted);
        }
      });

      // Add new file to files list
      const newFile = {
        id: response.data.fileId,
        filename: response.data.filename,
        fileType: response.data.fileType,
        fileSize: response.data.fileSize,
        uploadDate: new Date(),
        vectorCount: response.data.chunkCount
      };

      setFiles(prev => [...prev, newFile]);
      toast.success(`File "${file.name}" uploaded successfully!`);

      // Switch to files tab to show the uploaded file
      setActiveTab('files');
    } catch (error) {
      console.error('Error uploading file:', error);
      toast.error(`Failed to upload file: ${error.response?.data?.error || error.message}`);
    } finally {
      setUploading(false);
      setUploadProgress(0);
    }
  };

  // Handle file deletion
  const handleDeleteFile = async (fileId) => {
    try {
      await axios.delete(`${API_URL}/files/${fileId}?userId=${user.id}`);
      setFiles(prev => prev.filter(file => file.id !== fileId));
      toast.success('File deleted successfully');
    } catch (error) {
      console.error('Error deleting file:', error);
      toast.error('Failed to delete file');
    }
  };

  // Handle sending message
  const handleSendMessage = async () => {
    if (!input.trim() || isLoading) return;

    const userMessage = {
      id: generateId(),
      role: 'user',
      content: input.trim(),
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);
    setIsTyping(true);

    try {
      const response = await axios.post(`${API_URL}/chat/message`, {
        userId: user.id,
        message: userMessage.content,
        chatId
      });

      // Update chat ID if not set
      if (!chatId) {
        setChatId(response.data.chatId);
      }

      // Add assistant response to chat
      const assistantMessage = {
        id: generateId(),
        role: 'assistant',
        content: response.data.message,
        timestamp: new Date(),
        sources: response.data.sources
      };

      setMessages(prev => [...prev, assistantMessage]);

      // Convert response to speech if TTS is enabled
      if (isSpeaking) {
        await voiceService.speak(response.data.message);
      }
    } catch (error) {
      console.error('Error sending message:', error);

      const errorMessage = {
        id: generateId(),
        role: 'assistant',
        content: `I apologize, but I encountered an error: ${error.response?.data?.error || error.message}`,
        timestamp: new Date(),
      };

      setMessages(prev => [...prev, errorMessage]);
      toast.error('Failed to send message');
    } finally {
      setIsLoading(false);
      setIsTyping(false);
    }
  };

  // Handle voice transcript
  const handleVoiceTranscript = (transcript) => {
    setInput(transcript);
    toast.success('Voice input captured!');
  };

  // Handle speak toggle
  const handleSpeakToggle = () => {
    setIsSpeaking(!isSpeaking);
    if (!isSpeaking) {
      toast.success('Text-to-speech enabled');
    } else {
      toast.success('Text-to-speech disabled');
      voiceService.stopSpeaking();
    }
  };

  // Handle message speak
  const handleMessageSpeak = async (content) => {
    try {
      await voiceService.speak(content);
    } catch (error) {
      toast.error('Failed to speak message');
    }
  };

  // Handle feedback
  const handleFeedback = async (messageIndex, feedback) => {
    try {
      await axios.post(`${API_URL}/chat/feedback`, {
        chatId,
        messageIndex,
        feedback
      });

      // Update message with feedback
      setMessages(prev => {
        const updated = [...prev];
        updated[messageIndex] = { ...updated[messageIndex], feedback };
        return updated;
      });
    } catch (error) {
      console.error('Error submitting feedback:', error);
      toast.error('Failed to submit feedback');
    }
  };

  // Handle key press
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      <Toaster
        position="top-right"
        toastOptions={{
          duration: 3000,
          style: {
            background: '#363636',
            color: '#fff',
          },
        }}
      />

      {/* Header */}
      <motion.header
        initial={{ y: -20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        className="bg-white border-b border-gray-200 px-4 py-3 flex items-center justify-between shadow-sm"
      >
        <div className="flex items-center space-x-4">
          <button
            onClick={() => setSidebarOpen(!sidebarOpen)}
            className="lg:hidden p-2 hover:bg-gray-100 rounded-lg transition-colors"
          >
            {sidebarOpen ? <X className="w-5 h-5" /> : <Menu className="w-5 h-5" />}
          </button>

          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 bg-primary-600 rounded-lg flex items-center justify-center">
              <Bot className="w-5 h-5 text-white" />
            </div>
            <div>
              <h1 className="text-xl font-bold text-gray-900">RAG Assistant</h1>
              <p className="text-xs text-gray-500">Powered by Gemini AI</p>
            </div>
          </div>
        </div>

        <div className="flex items-center space-x-4">
          <div className="hidden sm:flex items-center space-x-2 text-sm text-gray-600">
            <User className="w-4 h-4" />
            <span>{user.name}</span>
          </div>

          <button className="p-2 hover:bg-gray-100 rounded-lg transition-colors">
            <Settings className="w-5 h-5 text-gray-600" />
          </button>
        </div>
      </motion.header>

      <div className="flex-1 flex overflow-hidden">
        {/* Sidebar */}
        <AnimatePresence>
          {sidebarOpen && (
            <motion.aside
              initial={{ x: -300, opacity: 0 }}
              animate={{ x: 0, opacity: 1 }}
              exit={{ x: -300, opacity: 0 }}
              transition={{ duration: 0.2 }}
              className="w-80 bg-white border-r border-gray-200 flex flex-col"
            >
              {/* Sidebar Header */}
              <div className="p-4 border-b border-gray-200">
                <div className="flex space-x-1">
                  <button
                    onClick={() => setActiveTab('upload')}
                    className={cn(
                      "flex-1 flex items-center justify-center space-x-2 py-2 px-3 rounded-lg text-sm font-medium transition-colors",
                      activeTab === 'upload'
                        ? "bg-primary-100 text-primary-700"
                        : "text-gray-600 hover:text-gray-900 hover:bg-gray-100"
                    )}
                  >
                    <UploadIcon className="w-4 h-4" />
                    <span>Upload</span>
                  </button>

                  <button
                    onClick={() => setActiveTab('files')}
                    className={cn(
                      "flex-1 flex items-center justify-center space-x-2 py-2 px-3 rounded-lg text-sm font-medium transition-colors",
                      activeTab === 'files'
                        ? "bg-primary-100 text-primary-700"
                        : "text-gray-600 hover:text-gray-900 hover:bg-gray-100"
                    )}
                  >
                    <FileText className="w-4 h-4" />
                    <span>Files ({files.length})</span>
                  </button>
                </div>
              </div>

              {/* Sidebar Content */}
              <div className="flex-1 overflow-y-auto p-4">
                {activeTab === 'upload' ? (
                  <FileUpload
                    onFileUpload={handleFileUpload}
                    uploading={uploading}
                    uploadProgress={uploadProgress}
                  />
                ) : (
                  <FileList
                    files={files}
                    onDeleteFile={handleDeleteFile}
                    loading={filesLoading}
                  />
                )}
              </div>
            </motion.aside>
          )}
        </AnimatePresence>

        {/* Main Chat Area */}
        <main className="flex-1 flex flex-col overflow-hidden">
          {/* Chat Messages */}
          <div
            ref={chatContainerRef}
            className="flex-1 overflow-y-auto p-4 space-y-4 scrollbar-hide"
          >
            <AnimatePresence>
              {messages.map((message, index) => (
                <ChatMessage
                  key={message.id}
                  message={message}
                  index={index}
                  onFeedback={handleFeedback}
                  onSpeak={handleMessageSpeak}
                  isLastMessage={index === messages.length - 1}
                />
              ))}
            </AnimatePresence>

            {/* Typing Indicator */}
            {isTyping && (
              <ChatMessage
                message={{ role: 'assistant', content: '', timestamp: new Date() }}
                index={-1}
                onFeedback={() => {}}
                onSpeak={() => {}}
                isTyping={true}
              />
            )}

            <div ref={messagesEndRef} />
          </div>

          {/* Input Area */}
          <div className="border-t border-gray-200 bg-white p-4">
            <div className="max-w-4xl mx-auto">
              <div className="flex items-end space-x-3">
                {/* Text Input */}
                <div className="flex-1 relative">
                  <textarea
                    ref={inputRef}
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={handleKeyPress}
                    placeholder="Type your message here... (Press Enter to send, Shift+Enter for new line)"
                    className="w-full px-4 py-3 pr-12 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent resize-none max-h-32 min-h-[48px]"
                    rows={1}
                    disabled={isLoading}
                    style={{
                      height: 'auto',
                      minHeight: '48px',
                      maxHeight: '128px'
                    }}
                    onInput={(e) => {
                      e.target.style.height = 'auto';
                      e.target.style.height = Math.min(e.target.scrollHeight, 128) + 'px';
                    }}
                  />

                  {/* Character count */}
                  {input.length > 0 && (
                    <div className="absolute bottom-1 right-3 text-xs text-gray-400">
                      {input.length}
                    </div>
                  )}
                </div>

                {/* Voice Input */}
                <VoiceInput
                  onTranscript={handleVoiceTranscript}
                  onSpeakToggle={handleSpeakToggle}
                  isSpeaking={isSpeaking}
                  disabled={isLoading}
                  className="relative"
                />

                {/* Send Button */}
                <motion.button
                  onClick={handleSendMessage}
                  disabled={!input.trim() || isLoading}
                  className={cn(
                    "p-3 rounded-xl transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2",
                    input.trim() && !isLoading
                      ? "bg-primary-600 hover:bg-primary-700 text-white shadow-lg"
                      : "bg-gray-200 text-gray-400 cursor-not-allowed"
                  )}
                  whileTap={{ scale: 0.95 }}
                  title="Send message"
                >
                  {isLoading ? (
                    <motion.div
                      animate={{ rotate: 360 }}
                      transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                      className="w-5 h-5"
                    >
                      <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                      </svg>
                    </motion.div>
                  ) : (
                    <Send className="w-5 h-5" />
                  )}
                </motion.button>
              </div>

              {/* Quick Actions */}
              {files.length > 0 && messages.length <= 1 && (
                <motion.div
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="mt-3 flex flex-wrap gap-2"
                >
                  <span className="text-xs text-gray-500">Quick questions:</span>
                  {[
                    "What's in my documents?",
                    "Summarize the key points",
                    "What are the main topics discussed?"
                  ].map((question, index) => (
                    <button
                      key={index}
                      onClick={() => setInput(question)}
                      className="text-xs px-3 py-1 bg-gray-100 hover:bg-gray-200 text-gray-700 rounded-full transition-colors"
                    >
                      {question}
                    </button>
                  ))}
                </motion.div>
              )}
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}

export default App;
