import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { ThumbsUp, ThumbsDown, Copy, Volume2, ChevronDown, ChevronUp, User, Bot } from 'lucide-react';
import { cn, formatMessageTime, copyToClipboard } from '../utils';
import toast from 'react-hot-toast';

const ChatMessage = ({ 
  message, 
  index, 
  onFeedback, 
  onSpeak, 
  isLastMessage,
  isTyping = false 
}) => {
  const [showSources, setShowSources] = useState(false);
  const [feedbackGiven, setFeedbackGiven] = useState(message.feedback || null);

  const isUser = message.role === 'user';
  const isAssistant = message.role === 'assistant';

  const handleCopy = async () => {
    const success = await copyToClipboard(message.content);
    if (success) {
      toast.success('Message copied to clipboard');
    } else {
      toast.error('Failed to copy message');
    }
  };

  const handleFeedback = (feedback) => {
    setFeedbackGiven(feedback);
    onFeedback(index, feedback);
    toast.success(feedback === 'positive' ? 'Thanks for the feedback!' : 'Feedback recorded');
  };

  const handleSpeak = () => {
    onSpeak(message.content);
  };

  if (isTyping) {
    return (
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="flex items-start space-x-3 mb-6"
      >
        <div className="flex-shrink-0 w-8 h-8 bg-primary-100 rounded-full flex items-center justify-center">
          <Bot className="w-5 h-5 text-primary-600" />
        </div>
        <div className="flex-1">
          <div className="bg-white rounded-2xl rounded-tl-sm px-4 py-3 shadow-sm border">
            <div className="flex space-x-1">
              <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
              <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
              <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
            </div>
          </div>
        </div>
      </motion.div>
    );
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3, delay: index * 0.1 }}
      className={cn(
        "flex items-start space-x-3 mb-6",
        isUser && "flex-row-reverse space-x-reverse"
      )}
    >
      {/* Avatar */}
      <div className={cn(
        "flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center",
        isUser ? "bg-primary-600" : "bg-primary-100"
      )}>
        {isUser ? (
          <User className="w-5 h-5 text-white" />
        ) : (
          <Bot className="w-5 h-5 text-primary-600" />
        )}
      </div>

      {/* Message Content */}
      <div className={cn(
        "flex-1 max-w-xs sm:max-w-md lg:max-w-lg xl:max-w-xl",
        isUser && "flex flex-col items-end"
      )}>
        <div className={cn(
          "rounded-2xl px-4 py-3 shadow-sm border",
          isUser 
            ? "bg-primary-600 text-white rounded-tr-sm" 
            : "bg-white text-gray-900 rounded-tl-sm"
        )}>
          <p className="text-sm leading-relaxed whitespace-pre-wrap">
            {message.content}
          </p>

          {/* Timestamp */}
          <p className={cn(
            "text-xs mt-2 opacity-70",
            isUser ? "text-primary-100" : "text-gray-500"
          )}>
            {formatMessageTime(message.timestamp)}
          </p>
        </div>

        {/* Sources */}
        {isAssistant && message.sources && message.sources.length > 0 && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            className="mt-2 w-full"
          >
            <button
              onClick={() => setShowSources(!showSources)}
              className="flex items-center space-x-2 text-xs text-gray-500 hover:text-gray-700 transition-colors"
            >
              <span>Sources ({message.sources.length})</span>
              {showSources ? (
                <ChevronUp className="w-3 h-3" />
              ) : (
                <ChevronDown className="w-3 h-3" />
              )}
            </button>

            {showSources && (
              <motion.div
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                className="mt-2 space-y-1"
              >
                {message.sources.map((source, i) => (
                  <div
                    key={i}
                    className="text-xs p-2 bg-gray-50 rounded-lg border"
                  >
                    <p className="text-gray-600 line-clamp-2">
                      {source.text}
                    </p>
                  </div>
                ))}
              </motion.div>
            )}
          </motion.div>
        )}

        {/* Action Buttons */}
        {isAssistant && (
          <div className="flex items-center space-x-2 mt-2">
            <button
              onClick={handleCopy}
              className="p-1.5 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
              title="Copy message"
            >
              <Copy className="w-4 h-4" />
            </button>

            <button
              onClick={handleSpeak}
              className="p-1.5 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
              title="Read aloud"
            >
              <Volume2 className="w-4 h-4" />
            </button>

            <div className="flex items-center space-x-1 ml-2">
              <button
                onClick={() => handleFeedback('positive')}
                className={cn(
                  "p-1.5 rounded-lg transition-colors",
                  feedbackGiven === 'positive'
                    ? "text-green-600 bg-green-100"
                    : "text-gray-400 hover:text-green-600 hover:bg-green-50"
                )}
                title="Good response"
              >
                <ThumbsUp className="w-4 h-4" />
              </button>

              <button
                onClick={() => handleFeedback('negative')}
                className={cn(
                  "p-1.5 rounded-lg transition-colors",
                  feedbackGiven === 'negative'
                    ? "text-red-600 bg-red-100"
                    : "text-gray-400 hover:text-red-600 hover:bg-red-50"
                )}
                title="Poor response"
              >
                <ThumbsDown className="w-4 h-4" />
              </button>
            </div>
          </div>
        )}
      </div>
    </motion.div>
  );
};

export default ChatMessage;
