import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

// Utility functions
const generateId = () => Math.random().toString(36).substr(2, 9);
const formatFileSize = (bytes) => {
  if (bytes === 0) return '0 Bytes';
  const k = 1024;
  const sizes = ['Bytes', 'KB', 'MB', 'GB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
};

function App() {
  // State for user authentication
  const [user] = useState({ id: '123456789012', name: 'Demo User' });

  // State for chat
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [chatId, setChatId] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  // State for file management
  const [files, setFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  // UI State
  const [activeTab, setActiveTab] = useState('upload'); // 'upload' or 'files'

  // Refs
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);

  // Initialize with greeting message
  useEffect(() => {
    setMessages([
      {
        id: generateId(),
        role: 'assistant',
        content: 'ğŸ‘‹ Hello! I\'m your AI assistant powered by Gemini AI and RAG technology. Upload documents and ask me questions - I\'ll provide contextual answers based on your files!',
        timestamp: new Date(),
      }
    ]);

    // Fetch user's files
    fetchFiles();
  }, []);

  // Scroll to bottom of messages
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  // Fetch user's files
  const fetchFiles = async () => {
    try {
      const response = await axios.get(`${API_URL}/files?userId=${user.id}`);
      setFiles(response.data.files || []);
    } catch (error) {
      console.error('Error fetching files:', error);
      alert('Failed to fetch files');
    }
  };

  // Handle file upload
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setUploading(true);
    setUploadProgress(0);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('userId', user.id);

    try {
      const response = await axios.post(`${API_URL}/files/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setUploadProgress(percentCompleted);
        }
      });

      // Add new file to files list
      const newFile = {
        id: response.data.fileId,
        filename: response.data.filename,
        fileType: response.data.fileType,
        fileSize: response.data.fileSize,
        uploadDate: new Date(),
        vectorCount: response.data.chunkCount
      };

      setFiles(prev => [...prev, newFile]);
      alert(`File "${file.name}" uploaded successfully!`);

      // Switch to files tab to show the uploaded file
      setActiveTab('files');

      // Clear file input
      event.target.value = '';
    } catch (error) {
      console.error('Error uploading file:', error);
      alert(`Failed to upload file: ${error.response?.data?.error || error.message}`);
    } finally {
      setUploading(false);
      setUploadProgress(0);
    }
  };

  // Handle file deletion
  const handleDeleteFile = async (fileId) => {
    if (window.confirm('Are you sure you want to delete this file?')) {
      try {
        await axios.delete(`${API_URL}/files/${fileId}?userId=${user.id}`);
        setFiles(prev => prev.filter(file => file.id !== fileId));
        alert('File deleted successfully');
      } catch (error) {
        console.error('Error deleting file:', error);
        alert('Failed to delete file');
      }
    }
  };

  // Handle sending message
  const handleSendMessage = async () => {
    if (!input.trim() || isLoading) return;

    const userMessage = {
      id: generateId(),
      role: 'user',
      content: input.trim(),
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await axios.post(`${API_URL}/chat/message`, {
        userId: user.id,
        message: userMessage.content,
        chatId
      });

      // Update chat ID if not set
      if (!chatId) {
        setChatId(response.data.chatId);
      }

      // Add assistant response to chat
      const assistantMessage = {
        id: generateId(),
        role: 'assistant',
        content: response.data.message,
        timestamp: new Date(),
        sources: response.data.sources
      };

      setMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      console.error('Error sending message:', error);

      const errorMessage = {
        id: generateId(),
        role: 'assistant',
        content: `I apologize, but I encountered an error: ${error.response?.data?.error || error.message}`,
        timestamp: new Date(),
      };

      setMessages(prev => [...prev, errorMessage]);
      alert('Failed to send message');
    } finally {
      setIsLoading(false);
    }
  };

  // Handle key press
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Format timestamp
  const formatTime = (date) => {
    return new Date(date).toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#f9fafb', display: 'flex', flexDirection: 'column' }}>
      {/* Header */}
      <header style={{
        backgroundColor: 'white',
        borderBottom: '1px solid #e5e7eb',
        padding: '1rem',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        boxShadow: '0 1px 2px 0 rgb(0 0 0 / 0.05)'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <div style={{
            width: '2rem',
            height: '2rem',
            backgroundColor: '#2563eb',
            borderRadius: '0.5rem',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            color: 'white',
            fontWeight: 'bold'
          }}>
            ğŸ¤–
          </div>
          <div>
            <h1 style={{ fontSize: '1.25rem', fontWeight: 'bold', color: '#111827', margin: 0 }}>
              RAG Assistant
            </h1>
            <p style={{ fontSize: '0.75rem', color: '#6b7280', margin: 0 }}>
              Powered by Gemini AI
            </p>
          </div>
        </div>

        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <span style={{ fontSize: '0.875rem', color: '#4b5563' }}>
            ğŸ‘¤ {user.name}
          </span>
        </div>
      </header>

      <div style={{ flex: 1, display: 'flex', overflow: 'hidden' }}>
        {/* Sidebar */}
        <aside style={{
          width: '20rem',
          backgroundColor: 'white',
          borderRight: '1px solid #e5e7eb',
          display: 'flex',
          flexDirection: 'column'
        }}>
          {/* Sidebar Header */}
          <div style={{ padding: '1rem', borderBottom: '1px solid #e5e7eb' }}>
            <div style={{ display: 'flex', gap: '0.25rem' }}>
              <button
                onClick={() => setActiveTab('upload')}
                style={{
                  flex: 1,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '0.5rem',
                  padding: '0.5rem 0.75rem',
                  borderRadius: '0.5rem',
                  fontSize: '0.875rem',
                  fontWeight: '500',
                  border: 'none',
                  cursor: 'pointer',
                  backgroundColor: activeTab === 'upload' ? '#dbeafe' : 'transparent',
                  color: activeTab === 'upload' ? '#1d4ed8' : '#4b5563'
                }}
              >
                ğŸ“¤ Upload
              </button>

              <button
                onClick={() => setActiveTab('files')}
                style={{
                  flex: 1,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '0.5rem',
                  padding: '0.5rem 0.75rem',
                  borderRadius: '0.5rem',
                  fontSize: '0.875rem',
                  fontWeight: '500',
                  border: 'none',
                  cursor: 'pointer',
                  backgroundColor: activeTab === 'files' ? '#dbeafe' : 'transparent',
                  color: activeTab === 'files' ? '#1d4ed8' : '#4b5563'
                }}
              >
                ğŸ“„ Files ({files.length})
              </button>
            </div>
          </div>

          {/* Sidebar Content */}
          <div style={{ flex: 1, overflowY: 'auto', padding: '1rem' }}>
            {activeTab === 'upload' ? (
              <div>
                <div style={{
                  border: '2px dashed #d1d5db',
                  borderRadius: '0.5rem',
                  padding: '2rem',
                  textAlign: 'center',
                  backgroundColor: uploading ? '#f3f4f6' : 'transparent'
                }}>
                  <div style={{ fontSize: '2rem', marginBottom: '1rem' }}>ğŸ“</div>
                  <p style={{ fontSize: '1rem', fontWeight: '500', color: '#111827', marginBottom: '0.5rem' }}>
                    Upload Documents
                  </p>
                  <p style={{ fontSize: '0.875rem', color: '#6b7280', marginBottom: '1rem' }}>
                    Drag and drop files here, or click to browse
                  </p>
                  <input
                    ref={fileInputRef}
                    type="file"
                    onChange={handleFileUpload}
                    accept=".pdf,.txt,.md,.png,.jpg,.jpeg,.webp"
                    style={{ display: 'none' }}
                    disabled={uploading}
                  />
                  <button
                    onClick={() => fileInputRef.current?.click()}
                    disabled={uploading}
                    className="btn-primary"
                    style={{ marginBottom: '0.5rem' }}
                  >
                    {uploading ? `Uploading... ${uploadProgress}%` : 'Select File'}
                  </button>
                  <p style={{ fontSize: '0.75rem', color: '#9ca3af' }}>
                    Supports PDF, TXT, MD, PNG, JPG, JPEG, WEBP
                  </p>
                </div>
              </div>
            ) : (
              <div>
                <h3 style={{ fontSize: '1rem', fontWeight: '600', color: '#111827', marginBottom: '1rem' }}>
                  Your Files
                </h3>
                {files.length === 0 ? (
                  <div style={{ textAlign: 'center', padding: '2rem' }}>
                    <div style={{ fontSize: '2rem', marginBottom: '1rem' }}>ğŸ“„</div>
                    <p style={{ color: '#6b7280', fontSize: '0.875rem' }}>No files uploaded yet</p>
                  </div>
                ) : (
                  <div style={{ display: 'flex', flexDirection: 'column', gap: '0.75rem' }}>
                    {files.map((file) => (
                      <div
                        key={file.id}
                        style={{
                          backgroundColor: 'white',
                          border: '1px solid #e5e7eb',
                          borderRadius: '0.5rem',
                          padding: '0.75rem',
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'space-between'
                        }}
                      >
                        <div style={{ flex: 1, minWidth: 0 }}>
                          <p style={{
                            fontSize: '0.875rem',
                            fontWeight: '500',
                            color: '#111827',
                            margin: 0,
                            overflow: 'hidden',
                            textOverflow: 'ellipsis',
                            whiteSpace: 'nowrap'
                          }}>
                            {file.filename}
                          </p>
                          <p style={{ fontSize: '0.75rem', color: '#6b7280', margin: 0 }}>
                            {formatFileSize(file.fileSize)} â€¢ {file.fileType.toUpperCase()}
                          </p>
                        </div>
                        <button
                          onClick={() => handleDeleteFile(file.id)}
                          style={{
                            background: 'none',
                            border: 'none',
                            color: '#ef4444',
                            cursor: 'pointer',
                            padding: '0.25rem',
                            borderRadius: '0.25rem'
                          }}
                          title="Delete file"
                        >
                          ğŸ—‘ï¸
                        </button>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </div>
        </aside>

        {/* Main Chat Area */}
        <main style={{ flex: 1, display: 'flex', flexDirection: 'column', overflow: 'hidden' }}>
          {/* Chat Messages */}
          <div style={{
            flex: 1,
            overflowY: 'auto',
            padding: '1rem',
            display: 'flex',
            flexDirection: 'column',
            gap: '1rem'
          }}>
            {messages.map((message, index) => (
              <div
                key={message.id}
                style={{
                  display: 'flex',
                  alignItems: 'flex-start',
                  gap: '0.75rem',
                  flexDirection: message.role === 'user' ? 'row-reverse' : 'row'
                }}
              >
                {/* Avatar */}
                <div style={{
                  width: '2rem',
                  height: '2rem',
                  borderRadius: '50%',
                  backgroundColor: message.role === 'user' ? '#2563eb' : '#f3f4f6',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '0.875rem',
                  flexShrink: 0
                }}>
                  {message.role === 'user' ? 'ğŸ‘¤' : 'ğŸ¤–'}
                </div>

                {/* Message */}
                <div style={{
                  maxWidth: '70%',
                  padding: '0.75rem 1rem',
                  borderRadius: '1rem',
                  backgroundColor: message.role === 'user' ? '#2563eb' : 'white',
                  color: message.role === 'user' ? 'white' : '#111827',
                  border: message.role === 'assistant' ? '1px solid #e5e7eb' : 'none',
                  wordWrap: 'break-word'
                }}>
                  <p style={{ margin: 0, lineHeight: 1.5 }}>
                    {message.content}
                  </p>
                  <p style={{
                    margin: '0.5rem 0 0 0',
                    fontSize: '0.75rem',
                    opacity: 0.7
                  }}>
                    {formatTime(message.timestamp)}
                  </p>

                  {/* Sources */}
                  {message.sources && message.sources.length > 0 && (
                    <div style={{ marginTop: '0.5rem' }}>
                      <p style={{ fontSize: '0.75rem', opacity: 0.8, margin: '0 0 0.25rem 0' }}>
                        Sources ({message.sources.length}):
                      </p>
                      {message.sources.slice(0, 2).map((source, i) => (
                        <div
                          key={i}
                          style={{
                            fontSize: '0.75rem',
                            padding: '0.25rem 0.5rem',
                            backgroundColor: 'rgba(0,0,0,0.1)',
                            borderRadius: '0.25rem',
                            marginBottom: '0.25rem',
                            opacity: 0.8
                          }}
                        >
                          {source.text}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            ))}

            {/* Loading indicator */}
            {isLoading && (
              <div style={{
                display: 'flex',
                alignItems: 'flex-start',
                gap: '0.75rem'
              }}>
                <div style={{
                  width: '2rem',
                  height: '2rem',
                  borderRadius: '50%',
                  backgroundColor: '#f3f4f6',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '0.875rem'
                }}>
                  ğŸ¤–
                </div>
                <div style={{
                  padding: '0.75rem 1rem',
                  borderRadius: '1rem',
                  backgroundColor: 'white',
                  border: '1px solid #e5e7eb'
                }}>
                  <div style={{ display: 'flex', gap: '0.25rem' }}>
                    <div style={{
                      width: '0.5rem',
                      height: '0.5rem',
                      backgroundColor: '#9ca3af',
                      borderRadius: '50%',
                      animation: 'bounce 1.4s infinite ease-in-out'
                    }}></div>
                    <div style={{
                      width: '0.5rem',
                      height: '0.5rem',
                      backgroundColor: '#9ca3af',
                      borderRadius: '50%',
                      animation: 'bounce 1.4s infinite ease-in-out 0.16s'
                    }}></div>
                    <div style={{
                      width: '0.5rem',
                      height: '0.5rem',
                      backgroundColor: '#9ca3af',
                      borderRadius: '50%',
                      animation: 'bounce 1.4s infinite ease-in-out 0.32s'
                    }}></div>
                  </div>
                </div>
              </div>
            )}

            <div ref={messagesEndRef} />
          </div>

          {/* Input Area */}
          <div style={{
            borderTop: '1px solid #e5e7eb',
            backgroundColor: 'white',
            padding: '1rem'
          }}>
            <div style={{ maxWidth: '56rem', margin: '0 auto' }}>
              <div style={{ display: 'flex', alignItems: 'flex-end', gap: '0.75rem' }}>
                {/* Text Input */}
                <div style={{ flex: 1, position: 'relative' }}>
                  <textarea
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={handleKeyPress}
                    placeholder="Type your message here... (Press Enter to send, Shift+Enter for new line)"
                    style={{
                      width: '100%',
                      padding: '0.75rem 1rem',
                      border: '1px solid #d1d5db',
                      borderRadius: '0.75rem',
                      resize: 'none',
                      minHeight: '3rem',
                      maxHeight: '8rem',
                      fontSize: '0.875rem',
                      fontFamily: 'inherit'
                    }}
                    rows={1}
                    disabled={isLoading}
                  />
                </div>

                {/* Send Button */}
                <button
                  onClick={handleSendMessage}
                  disabled={!input.trim() || isLoading}
                  style={{
                    padding: '0.75rem',
                    borderRadius: '0.75rem',
                    border: 'none',
                    backgroundColor: input.trim() && !isLoading ? '#2563eb' : '#e5e7eb',
                    color: input.trim() && !isLoading ? 'white' : '#9ca3af',
                    cursor: input.trim() && !isLoading ? 'pointer' : 'not-allowed',
                    fontSize: '1.25rem',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    minWidth: '3rem',
                    minHeight: '3rem'
                  }}
                  title="Send message"
                >
                  {isLoading ? 'â³' : 'ğŸ“¤'}
                </button>
              </div>

              {/* Quick Actions */}
              {files.length > 0 && messages.length <= 1 && (
                <div style={{ marginTop: '0.75rem', display: 'flex', flexWrap: 'wrap', gap: '0.5rem' }}>
                  <span style={{ fontSize: '0.75rem', color: '#6b7280' }}>Quick questions:</span>
                  {[
                    "What's in my documents?",
                    "Summarize the key points",
                    "What are the main topics discussed?"
                  ].map((question, index) => (
                    <button
                      key={index}
                      onClick={() => setInput(question)}
                      style={{
                        fontSize: '0.75rem',
                        padding: '0.25rem 0.75rem',
                        backgroundColor: '#f3f4f6',
                        color: '#374151',
                        border: 'none',
                        borderRadius: '9999px',
                        cursor: 'pointer'
                      }}
                    >
                      {question}
                    </button>
                  ))}
                </div>
              )}
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}

export default App;
