// Frontend implementation for the React application
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

// API configuration
const API_URL = 'http://localhost:5000/api';

function App() {
  // State for user authentication
  const [user, setUser] = useState({ id: '123456789012', name: 'Aman' }); // Mock user for demo

  // State for chat
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [chatId, setChatId] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  // State for file management
  const [files, setFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  // State for voice
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [speechEnabled, setSpeechEnabled] = useState(false);
  const [voiceSupported, setVoiceSupported] = useState(false);
  const [speechRecognition, setSpeechRecognition] = useState(null);
  const [speechSynthesis, setSpeechSynthesis] = useState(null);

  // Refs
  const fileInputRef = useRef(null);
  const messagesEndRef = useRef(null);
  const recognitionRef = useRef(null);
  const synthRef = useRef(null);

  // Initialize voice capabilities
  useEffect(() => {
    // Check for Web Speech API support
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    const speechSynth = window.speechSynthesis;

    if (SpeechRecognition) {
      const recognition = new SpeechRecognition();
      recognition.continuous = false;
      recognition.interimResults = false;
      recognition.lang = 'en-US';

      recognition.onstart = () => {
        setIsListening(true);
      };

      recognition.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setInput(transcript);
        setIsListening(false);
        // Remove listening message
        setMessages(prev => prev.filter(msg => msg.content !== 'Listening... Speak now!'));
      };

      recognition.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setIsListening(false);
        // Remove listening message and add error message
        setMessages(prev => {
          const filtered = prev.filter(msg => msg.content !== 'Listening... Speak now!');
          return [...filtered, {
            role: 'assistant',
            content: `Voice recognition error: ${event.error}. Please try again.`,
            timestamp: new Date(),
          }];
        });
      };

      recognition.onend = () => {
        setIsListening(false);
      };

      recognitionRef.current = recognition;
      setSpeechRecognition(recognition);
      setVoiceSupported(true);
    }

    if (speechSynth) {
      synthRef.current = speechSynth;
      setSpeechSynthesis(speechSynth);
    }

    // Initialize with greeting message
    setMessages([
      {
        role: 'assistant',
        content: `Hello ${user.name}! I'm your AI assistant. You can ask me questions, and I'll use your uploaded documents to provide relevant answers. ${SpeechRecognition ? 'You can also speak to me by clicking the microphone button.' : ''}`,
        timestamp: new Date(),
      }
    ]);

    // Fetch user's files
    fetchFiles();
  }, [user.name]);

  // Scroll to bottom of messages
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Fetch user's files
  const fetchFiles = async () => {
    try {
      const response = await axios.get(`${API_URL}/files?userId=${user.id}`);
      setFiles(response.data.files || []);
    } catch (error) {
      console.error('Error fetching files:', error);
    }
  };

  // Handle file upload
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);
    formData.append('userId', user.id);

    setUploading(true);
    setUploadProgress(0);

    try {
      const response = await axios.post(`${API_URL}/files/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setUploadProgress(percentCompleted);
        }
      });

      // Add new file to files list
      setFiles([...files, {
        id: response.data.fileId,
        filename: response.data.filename,
        fileType: response.data.fileType,
        fileSize: response.data.fileSize,
        uploadDate: new Date(),
        vectorCount: response.data.chunkCount
      }]);

      // Add system message about successful upload
      setMessages([...messages, {
        role: 'assistant',
        content: `File "${response.data.filename}" uploaded successfully. You can now ask questions about its content.`,
        timestamp: new Date(),
      }]);
    } catch (error) {
      console.error('Error uploading file:', error);

      // Add error message
      setMessages([...messages, {
        role: 'assistant',
        content: `Error uploading file: ${error.response?.data?.error || error.message}`,
        timestamp: new Date(),
      }]);
    } finally {
      setUploading(false);
      setUploadProgress(0);
      // Reset file input
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  // Handle file deletion
  const handleDeleteFile = async (fileId) => {
    try {
      await axios.delete(`${API_URL}/files/${fileId}?userId=${user.id}`);

      // Remove file from files list
      setFiles(files.filter(file => file.id !== fileId));

      // Add system message about successful deletion
      setMessages([...messages, {
        role: 'assistant',
        content: 'File deleted successfully.',
        timestamp: new Date(),
      }]);
    } catch (error) {
      console.error('Error deleting file:', error);

      // Add error message
      setMessages([...messages, {
        role: 'assistant',
        content: `Error deleting file: ${error.response?.data?.error || error.message}`,
        timestamp: new Date(),
      }]);
    }
  };

  // Handle sending message
  const handleSendMessage = async () => {
    if (!input.trim()) return;

    // Add user message to chat
    const userMessage = {
      role: 'user',
      content: input,
      timestamp: new Date(),
    };

    setMessages([...messages, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await axios.post(`${API_URL}/chat/message`, {
        userId: user.id,
        message: userMessage.content,
        chatId
      });

      // Update chat ID if not set
      if (!chatId) {
        setChatId(response.data.chatId);
      }

      // Add assistant response to chat
      const assistantMessage = {
        role: 'assistant',
        content: response.data.message,
        timestamp: new Date(),
        sources: response.data.sources
      };

      setMessages(prev => [...prev, assistantMessage]);

      // Convert response to speech if TTS is enabled
      if (speechEnabled) {
        // Optimize text for speech and then speak it
        optimizeAndSpeak(response.data.message);
      }
    } catch (error) {
      console.error('Error sending message:', error);

      // Add error message
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: `Error: ${error.response?.data?.error || error.message}`,
        timestamp: new Date(),
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle voice input
  const handleVoiceInput = () => {
    if (!voiceSupported) {
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: 'Voice recognition is not supported in your browser. Please use Chrome, Edge, or Safari.',
        timestamp: new Date(),
      }]);
      return;
    }

    if (!isListening) {
      try {
        recognitionRef.current.start();
        setMessages(prev => [...prev, {
          role: 'assistant',
          content: 'Listening... Speak now!',
          timestamp: new Date(),
        }]);
      } catch (error) {
        console.error('Error starting voice recognition:', error);
        setMessages(prev => [...prev, {
          role: 'assistant',
          content: 'Error starting voice recognition. Please try again.',
          timestamp: new Date(),
        }]);
      }
    } else {
      recognitionRef.current.stop();
      setIsListening(false);
      // Remove listening message
      setMessages(prev => prev.filter(msg => msg.content !== 'Listening... Speak now!'));
    }
  };

  // Handle text-to-speech
  const speakText = (text) => {
    try {
      if (!synthRef.current) {
        console.warn('Speech synthesis not available');
        return;
      }

      // Stop any current speech
      synthRef.current.cancel();

      // Create utterance
      const utterance = new SpeechSynthesisUtterance(text);

      // Configure voice settings
      utterance.rate = 0.9;
      utterance.pitch = 1;
      utterance.volume = 0.8;

      // Try to use a more natural voice if available
      const voices = synthRef.current.getVoices();
      const preferredVoice = voices.find(voice =>
        voice.name.includes('Google') ||
        voice.name.includes('Microsoft') ||
        voice.lang.startsWith('en')
      );

      if (preferredVoice) {
        utterance.voice = preferredVoice;
      }

      utterance.onstart = () => {
        setIsSpeaking(true);
      };

      utterance.onend = () => {
        setIsSpeaking(false);
      };

      utterance.onerror = (event) => {
        console.error('Speech synthesis error:', event.error);
        setIsSpeaking(false);
      };

      synthRef.current.speak(utterance);
    } catch (error) {
      console.error('Error with text-to-speech:', error);
      setIsSpeaking(false);
    }
  };

  // Toggle text-to-speech
  const toggleTTS = () => {
    if (speechEnabled) {
      // Stop current speech if any
      if (synthRef.current) {
        synthRef.current.cancel();
      }
      setIsSpeaking(false);
    }
    setSpeechEnabled(!speechEnabled);
  };

  // Stop speech
  const stopSpeech = () => {
    if (synthRef.current) {
      synthRef.current.cancel();
    }
    setIsSpeaking(false);
  };

  // Optimize text for speech and then speak it
  const optimizeAndSpeak = async (text) => {
    try {
      // Try to optimize text using the backend service
      const response = await axios.post(`${API_URL}/voice/optimize`, { text });
      const optimizedText = response.data.optimizedText || text;
      speakText(optimizedText);
    } catch (error) {
      console.warn('Text optimization failed, using original text:', error);
      // Fallback to original text if optimization fails
      speakText(text);
    }
  };

  // Process voice command with enhanced understanding
  const processVoiceCommand = async (command) => {
    try {
      const response = await axios.post(`${API_URL}/voice/command`, {
        command,
        userId: user.id
      });

      if (response.data.success) {
        const { intent, processedText, suggestions } = response.data;

        // Handle different intents
        switch (intent) {
          case 'upload':
            setMessages(prev => [...prev, {
              role: 'assistant',
              content: 'I understand you want to upload a file. Please use the file upload button in the sidebar.',
              timestamp: new Date(),
            }]);
            break;
          case 'help':
            setMessages(prev => [...prev, {
              role: 'assistant',
              content: 'I can help you with uploading documents, answering questions about your files, and general conversation. What would you like to know?',
              timestamp: new Date(),
            }]);
            break;
          default:
            // For chat intent, use the processed text
            setInput(processedText);
            break;
        }

        // Show suggestions if available
        if (suggestions && suggestions.length > 0) {
          setMessages(prev => [...prev, {
            role: 'assistant',
            content: `Suggestions: ${suggestions.join(', ')}`,
            timestamp: new Date(),
          }]);
        }
      }
    } catch (error) {
      console.warn('Voice command processing failed:', error);
      // Fallback to using the command as regular input
      setInput(command);
    }
  };

  // Handle feedback
  const handleFeedback = async (messageIndex, feedback, reportReason = null) => {
    try {
      await axios.post(`${API_URL}/chat/feedback`, {
        chatId,
        messageIndex,
        feedback,
        reportReason
      });

      // Update message with feedback
      const updatedMessages = [...messages];
      updatedMessages[messageIndex].feedback = feedback;
      setMessages(updatedMessages);

      // Add feedback confirmation message
      if (feedback === 'negative' && reportReason) {
        setMessages([...updatedMessages, {
          role: 'assistant',
          content: 'Thank you for your feedback. Your report has been submitted for review.',
          timestamp: new Date(),
        }]);
      }
    } catch (error) {
      console.error('Error submitting feedback:', error);
    }
  };

  // Format file size
  const formatFileSize = (bytes) => {
    if (bytes < 1024) return bytes + ' B';
    else if (bytes < 1048576) return (bytes / 1024).toFixed(1) + ' KB';
    else return (bytes / 1048576).toFixed(1) + ' MB';
  };

  // Format date
  const formatDate = (date) => {
    if (typeof date === 'string') {
      date = new Date(date);
    }
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString();
  };

  // Get file type icon
  const getFileTypeIcon = (fileType) => {
    switch (fileType) {
      case 'pdf': return '📄';
      case 'txt': return '📝';
      case 'md': return '📋';
      case 'png':
      case 'jpg':
      case 'jpeg':
      case 'webp':
        return '🖼️';
      default: return '📁';
    }
  };

  return (
    <div className="app">
      <header className="header">
        <h1>RAG-Enabled AI Chatbot</h1>
        <div className="user-info">
          <span>Logged in as: {user.name}</span>
        </div>
      </header>

      <div className="main-container">
        <div className="sidebar">
          <div className="file-upload">
            <h2>Upload Files</h2>
            <p>Supported formats: PDF, TXT, MD, PNG, JPG, JPEG, WEBP</p>
            <input
              type="file"
              ref={fileInputRef}
              onChange={handleFileUpload}
              accept=".pdf,.txt,.md,.png,.jpg,.jpeg,.webp"
              disabled={uploading}
            />
            <button
              onClick={() => fileInputRef.current.click()}
              disabled={uploading}
              className="upload-button"
            >
              {uploading ? `Uploading... ${uploadProgress}%` : 'Select File'}
            </button>
            {uploading && (
              <div className="progress-bar">
                <div
                  className="progress"
                  style={{ width: `${uploadProgress}%` }}
                ></div>
              </div>
            )}
          </div>

          <div className="file-list">
            <h2>Your Files</h2>
            {files.length === 0 ? (
              <p>No files uploaded yet.</p>
            ) : (
              <ul>
                {files.map(file => (
                  <li key={file.id} className="file-item">
                    <div className="file-info">
                      <span className="file-icon">{getFileTypeIcon(file.fileType)}</span>
                      <div className="file-details">
                        <span className="file-name">{file.filename}</span>
                        <span className="file-meta">
                          {formatFileSize(file.fileSize)} • {formatDate(file.uploadDate)}
                        </span>
                      </div>
                    </div>
                    <button
                      onClick={() => handleDeleteFile(file.id)}
                      className="delete-button"
                    >
                      🗑️
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>

        <div className="chat-container">
          <div className="messages">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`message ${message.role === 'user' ? 'user-message' : 'assistant-message'}`}
              >
                <div className="message-content">
                  {message.content}

                  {message.sources && message.sources.length > 0 && (
                    <div className="message-sources">
                      <details>
                        <summary>Sources ({message.sources.length})</summary>
                        <ul>
                          {message.sources.map((source, i) => (
                            <li key={i}>{source.text}</li>
                          ))}
                        </ul>
                      </details>
                    </div>
                  )}
                </div>

                <div className="message-meta">
                  <span className="message-time">
                    {formatDate(message.timestamp)}
                  </span>

                  {message.role === 'assistant' && index > 0 && (
                    <div className="message-feedback">
                      <button
                        onClick={() => handleFeedback(index, 'positive')}
                        className={`feedback-button ${message.feedback === 'positive' ? 'active' : ''}`}
                      >
                        👍
                      </button>
                      <button
                        onClick={() => {
                          const reason = prompt('Please provide a reason for reporting this response:');
                          if (reason) {
                            handleFeedback(index, 'negative', reason);
                          }
                        }}
                        className={`feedback-button ${message.feedback === 'negative' ? 'active' : ''}`}
                      >
                        👎
                      </button>
                    </div>
                  )}
                </div>
              </div>
            ))}

            {isLoading && (
              <div className="message assistant-message">
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            )}

            <div ref={messagesEndRef} />
          </div>

          <div className="input-container">
            <textarea
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type your message here..."
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  handleSendMessage();
                }
              }}
              disabled={isLoading || isListening}
            />

            <div className="input-buttons">
              <button
                onClick={handleVoiceInput}
                className={`voice-button ${isListening ? 'listening' : ''}`}
                title={voiceSupported ? (isListening ? "Stop listening" : "Voice input") : "Voice not supported"}
                disabled={!voiceSupported}
              >
                {isListening ? '🔴' : '🎤'}
              </button>

              <button
                onClick={toggleTTS}
                className={`tts-button ${speechEnabled ? 'active' : ''}`}
                title={speechEnabled ? "Disable text-to-speech" : "Enable text-to-speech"}
              >
                {speechEnabled ? '🔊' : '🔇'}
              </button>

              {isSpeaking && (
                <button
                  onClick={stopSpeech}
                  className="stop-speech-button"
                  title="Stop speaking"
                >
                  ⏹️
                </button>
              )}

              <button
                onClick={handleSendMessage}
                disabled={!input.trim() || isLoading}
                className="send-button"
              >
                Send
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
