import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

// Utility functions
const generateId = () => Math.random().toString(36).substr(2, 9);
const formatFileSize = (bytes) => {
  if (bytes === 0) return '0 Bytes';
  const k = 1024;
  const sizes = ['Bytes', 'KB', 'MB', 'GB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
};

function App() {
  // State for user authentication
  const [user] = useState({ id: '123456789012', name: 'Demo User' });

  // State for chat
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [chatId, setChatId] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  // State for file management
  const [files, setFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  // UI State
  const [activeTab, setActiveTab] = useState('upload'); // 'upload' or 'files'

  // Refs
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);

  // Initialize with greeting message
  useEffect(() => {
    setMessages([
      {
        id: generateId(),
        role: 'assistant',
        content: 'ðŸ‘‹ Hello! I\'m your AI assistant powered by Gemini AI and RAG technology. Upload documents and ask me questions - I\'ll provide contextual answers based on your files!',
        timestamp: new Date(),
      }
    ]);

    // Fetch user's files
    fetchFiles();
  }, []);

  // Scroll to bottom of messages
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  // Fetch user's files
  const fetchFiles = async () => {
    try {
      const response = await axios.get(`${API_URL}/files?userId=${user.id}`);
      setFiles(response.data.files || []);
    } catch (error) {
      console.error('Error fetching files:', error);
      alert('Failed to fetch files');
    }
  };

  // Handle file upload
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setUploading(true);
    setUploadProgress(0);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('userId', user.id);

    try {
      const response = await axios.post(`${API_URL}/files/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setUploadProgress(percentCompleted);
        }
      });

      // Add new file to files list
      const newFile = {
        id: response.data.fileId,
        filename: response.data.filename,
        fileType: response.data.fileType,
        fileSize: response.data.fileSize,
        uploadDate: new Date(),
        vectorCount: response.data.chunkCount
      };

      setFiles(prev => [...prev, newFile]);
      alert(`File "${file.name}" uploaded successfully!`);

      // Switch to files tab to show the uploaded file
      setActiveTab('files');

      // Clear file input
      event.target.value = '';
    } catch (error) {
      console.error('Error uploading file:', error);
      alert(`Failed to upload file: ${error.response?.data?.error || error.message}`);
    } finally {
      setUploading(false);
      setUploadProgress(0);
    }
  };

  // Handle file deletion
  const handleDeleteFile = async (fileId) => {
    if (window.confirm('Are you sure you want to delete this file?')) {
      try {
        await axios.delete(`${API_URL}/files/${fileId}?userId=${user.id}`);
        setFiles(prev => prev.filter(file => file.id !== fileId));
        alert('File deleted successfully');
      } catch (error) {
        console.error('Error deleting file:', error);
        alert('Failed to delete file');
      }
    }
  };

  // Handle sending message
  const handleSendMessage = async () => {
    if (!input.trim() || isLoading) return;

    const userMessage = {
      id: generateId(),
      role: 'user',
      content: input.trim(),
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await axios.post(`${API_URL}/chat/message`, {
        userId: user.id,
        message: userMessage.content,
        chatId
      });

      // Update chat ID if not set
      if (!chatId) {
        setChatId(response.data.chatId);
      }

      // Add assistant response to chat
      const assistantMessage = {
        id: generateId(),
        role: 'assistant',
        content: response.data.message,
        timestamp: new Date(),
        sources: response.data.sources
      };

      setMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      console.error('Error sending message:', error);

      const errorMessage = {
        id: generateId(),
        role: 'assistant',
        content: `I apologize, but I encountered an error: ${error.response?.data?.error || error.message}`,
        timestamp: new Date(),
      };

      setMessages(prev => [...prev, errorMessage]);
      alert('Failed to send message');
    } finally {
      setIsLoading(false);
    }
  };

  // Handle key press
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Format timestamp
  const formatTime = (date) => {
    return new Date(date).toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#f9fafb', display: 'flex', flexDirection: 'column' }}>
      {/* Header */}
      <header style={{
        backgroundColor: 'white',
        borderBottom: '1px solid #e5e7eb',
        padding: '1rem',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        boxShadow: '0 1px 2px 0 rgb(0 0 0 / 0.05)'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <div style={{
            width: '2rem',
            height: '2rem',
            backgroundColor: '#2563eb',
            borderRadius: '0.5rem',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            color: 'white',
            fontWeight: 'bold'
          }}>
            ðŸ¤–
          </div>
          <div>
            <h1 style={{ fontSize: '1.25rem', fontWeight: 'bold', color: '#111827', margin: 0 }}>
              RAG Assistant
            </h1>
            <p style={{ fontSize: '0.75rem', color: '#6b7280', margin: 0 }}>
              Powered by Gemini AI
            </p>
          </div>
        </div>

        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <span style={{ fontSize: '0.875rem', color: '#4b5563' }}>
            ðŸ‘¤ {user.name}
          </span>
        </div>
      </header>

      <div className="flex-1 flex overflow-hidden">
        {/* Sidebar */}
        <AnimatePresence>
          {sidebarOpen && (
            <motion.aside
              initial={{ x: -300, opacity: 0 }}
              animate={{ x: 0, opacity: 1 }}
              exit={{ x: -300, opacity: 0 }}
              transition={{ duration: 0.2 }}
              className="w-80 bg-white border-r border-gray-200 flex flex-col"
            >
              {/* Sidebar Header */}
              <div className="p-4 border-b border-gray-200">
                <div className="flex space-x-1">
                  <button
                    onClick={() => setActiveTab('upload')}
                    className={cn(
                      "flex-1 flex items-center justify-center space-x-2 py-2 px-3 rounded-lg text-sm font-medium transition-colors",
                      activeTab === 'upload'
                        ? "bg-primary-100 text-primary-700"
                        : "text-gray-600 hover:text-gray-900 hover:bg-gray-100"
                    )}
                  >
                    <UploadIcon className="w-4 h-4" />
                    <span>Upload</span>
                  </button>

                  <button
                    onClick={() => setActiveTab('files')}
                    className={cn(
                      "flex-1 flex items-center justify-center space-x-2 py-2 px-3 rounded-lg text-sm font-medium transition-colors",
                      activeTab === 'files'
                        ? "bg-primary-100 text-primary-700"
                        : "text-gray-600 hover:text-gray-900 hover:bg-gray-100"
                    )}
                  >
                    <FileText className="w-4 h-4" />
                    <span>Files ({files.length})</span>
                  </button>
                </div>
              </div>

              {/* Sidebar Content */}
              <div className="flex-1 overflow-y-auto p-4">
                {activeTab === 'upload' ? (
                  <FileUpload
                    onFileUpload={handleFileUpload}
                    uploading={uploading}
                    uploadProgress={uploadProgress}
                  />
                ) : (
                  <FileList
                    files={files}
                    onDeleteFile={handleDeleteFile}
                    loading={filesLoading}
                  />
                )}
              </div>
            </motion.aside>
          )}
        </AnimatePresence>

        {/* Main Chat Area */}
        <main className="flex-1 flex flex-col overflow-hidden">
          {/* Chat Messages */}
          <div
            ref={chatContainerRef}
            className="flex-1 overflow-y-auto p-4 space-y-4 scrollbar-hide"
          >
            <AnimatePresence>
              {messages.map((message, index) => (
                <ChatMessage
                  key={message.id}
                  message={message}
                  index={index}
                  onFeedback={handleFeedback}
                  onSpeak={handleMessageSpeak}
                  isLastMessage={index === messages.length - 1}
                />
              ))}
            </AnimatePresence>

            {/* Typing Indicator */}
            {isTyping && (
              <ChatMessage
                message={{ role: 'assistant', content: '', timestamp: new Date() }}
                index={-1}
                onFeedback={() => {}}
                onSpeak={() => {}}
                isTyping={true}
              />
            )}

            <div ref={messagesEndRef} />
          </div>

          {/* Input Area */}
          <div className="border-t border-gray-200 bg-white p-4">
            <div className="max-w-4xl mx-auto">
              <div className="flex items-end space-x-3">
                {/* Text Input */}
                <div className="flex-1 relative">
                  <textarea
                    ref={inputRef}
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={handleKeyPress}
                    placeholder="Type your message here... (Press Enter to send, Shift+Enter for new line)"
                    className="w-full px-4 py-3 pr-12 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent resize-none max-h-32 min-h-[48px]"
                    rows={1}
                    disabled={isLoading}
                    style={{
                      height: 'auto',
                      minHeight: '48px',
                      maxHeight: '128px'
                    }}
                    onInput={(e) => {
                      e.target.style.height = 'auto';
                      e.target.style.height = Math.min(e.target.scrollHeight, 128) + 'px';
                    }}
                  />

                  {/* Character count */}
                  {input.length > 0 && (
                    <div className="absolute bottom-1 right-3 text-xs text-gray-400">
                      {input.length}
                    </div>
                  )}
                </div>

                {/* Voice Input */}
                <VoiceInput
                  onTranscript={handleVoiceTranscript}
                  onSpeakToggle={handleSpeakToggle}
                  isSpeaking={isSpeaking}
                  disabled={isLoading}
                  className="relative"
                />

                {/* Send Button */}
                <motion.button
                  onClick={handleSendMessage}
                  disabled={!input.trim() || isLoading}
                  className={cn(
                    "p-3 rounded-xl transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2",
                    input.trim() && !isLoading
                      ? "bg-primary-600 hover:bg-primary-700 text-white shadow-lg"
                      : "bg-gray-200 text-gray-400 cursor-not-allowed"
                  )}
                  whileTap={{ scale: 0.95 }}
                  title="Send message"
                >
                  {isLoading ? (
                    <motion.div
                      animate={{ rotate: 360 }}
                      transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                      className="w-5 h-5"
                    >
                      <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                      </svg>
                    </motion.div>
                  ) : (
                    <Send className="w-5 h-5" />
                  )}
                </motion.button>
              </div>

              {/* Quick Actions */}
              {files.length > 0 && messages.length <= 1 && (
                <motion.div
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="mt-3 flex flex-wrap gap-2"
                >
                  <span className="text-xs text-gray-500">Quick questions:</span>
                  {[
                    "What's in my documents?",
                    "Summarize the key points",
                    "What are the main topics discussed?"
                  ].map((question, index) => (
                    <button
                      key={index}
                      onClick={() => setInput(question)}
                      className="text-xs px-3 py-1 bg-gray-100 hover:bg-gray-200 text-gray-700 rounded-full transition-colors"
                    >
                      {question}
                    </button>
                  ))}
                </motion.div>
              )}
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}

export default App;
